// time limited，用字符串操作比较慢
func repeatedSubstringPattern(_ s: String) -> Bool {
    if s.count <= 1 {
        return false
    }
    
    var step = s.count / 2
    while step >= 1 {

        if s.count % step == 0 {
            let group = s.count / step

            let startIndex = s.startIndex
            let endIndex = s.index(startIndex, offsetBy: step)
            
            let repeated = s[startIndex..<endIndex]
            
            var str = ""
            var i = 0
            while i < group {
                str.append(contentsOf: repeated)
                
                i += 1
            }
            
            if str == s {
                return true
            }
        }
        
        step -= 1
    }
    
    return false
}

// aacepted
func repeatedSubstringPattern_2(_ s: String) -> Bool {
    if s.count <= 1 {
        return false
    }
    
    var list = Array(s)
    
    var step = list.count / 2
    while step >= 1 {
        
        if list.count % step == 0 {
            let group = list.count / step
            
            let repeated = list[0..<step]
            
            var result = Array("")
            var i = 0
            while i < group {
                result.append(contentsOf: repeated)
                
                i += 1
            }
            
            if result.elementsEqual(list) {
                return true
            }
        }
        
        step -= 1
    }
    
    return false
}

let s = ""
print(repeatedSubstringPattern(s))
print(repeatedSubstringPattern_2(s))

